"use client";

import React, { forwardRef, useRef, useContext } from "react";

import { cn } from "../lib/utils";
import { AnimatedBeam } from "./AnimatedBeam";
// @ts-ignore
import { ThemeContext } from "@/context/ThemeContext";

type ThemeContextType = {
  isDarkMode?: boolean;
};


const Circle = forwardRef<
  HTMLDivElement,
  { className?: string; children?: React.ReactNode }
>(({ className, children }, ref) => {
  return (
    <div
      ref={ref}
      className={cn(
        "z-10 flex size-22 items-center justify-center  p-3 ",
        className
      )}
    >
      {children}
    </div>
  );
});
export function AnimatedBeamDemo() {

  const themeCtx = useContext(ThemeContext) as ThemeContextType;

  const isDarkMode = themeCtx?.isDarkMode ?? true;
  const containerRef = useRef<HTMLDivElement>(null);

  const div1Ref = useRef<HTMLDivElement>(null);
  const div2Ref = useRef<HTMLDivElement>(null);
  const div3Ref = useRef<HTMLDivElement>(null);
  const div4Ref = useRef<HTMLDivElement>(null);
  const div5Ref = useRef<HTMLDivElement>(null);
  const div6Ref = useRef<HTMLDivElement>(null);
  const div7Ref = useRef<HTMLDivElement>(null);

  return (
    <div
      className="relative flex h-[600px] w-full items-center justify-center overflow-hidden p-10"
      ref={containerRef}
    >
      <div className="flex size-full max-h-[400px] max-w-300 flex-col items-stretch justify-between gap-10">
        <div className="flex flex-row items-center justify-between">
          <Circle ref={div1Ref}>
            <Icons.html />
          </Circle>
          <Circle ref={div5Ref}>
            <Icons.css />
          </Circle>
        </div>
        <div className="flex flex-row items-center justify-between">
          <Circle ref={div2Ref}>
            <Icons.js />
          </Circle>
          <Circle
            ref={div4Ref}
            className={`cursor-target rounded-2xl w-[280px] h-[120px] transition-all duration-300 
    ${isDarkMode ? "bg-zinc-800" : "bg-zinc-200"}`}
          >
            <span
className={`
  whitespace-pre-wrap 
  bg-clip-text 
  text-center 
  text-4xl sm:text-6xl lg:text-8xl 
  font-semibold font-lyrae 
  leading-tight sm:leading-none 
  text-transparent      ${isDarkMode
                  ? "bg-gradient-to-b from-zinc-100 to-zinc-900"
                  : "bg-gradient-to-b from-zinc-800 to-zinc-100"
                }`}
            >
              Skills
            </span>
          </Circle>


          <Circle ref={div6Ref}>
            <Icons.react />
          </Circle>
        </div>
        <div className="flex flex-row items-center justify-between">
          <Circle ref={div3Ref}>
            <Icons.tailwind />
          </Circle>
          <Circle ref={div7Ref}>
            <Icons.laravel />
          </Circle>
        </div>
      </div>

      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div1Ref}
        toRef={div4Ref}
        curvature={-15}
        endYOffset={-10}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div2Ref}
        toRef={div4Ref}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div3Ref}
        toRef={div4Ref}
        curvature={15}
        endYOffset={10}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div5Ref}
        toRef={div4Ref}
        curvature={-15}
        endYOffset={-10}
        reverse
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div6Ref}
        toRef={div4Ref}
        reverse
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div7Ref}
        toRef={div4Ref}
        curvature={15}
        endYOffset={10}
        reverse
      />
    </div>
  );
}

